<?PHP /*###################################################################		Author: Michael Heil	Description: PHP script to fetch all files from xml directory			ChangeLog: Changes						Version		Date			   inital Version 				1.0 		12.09.2013		################################################################### */?><?phpif ($handle = opendir('../xmlFiles/')) {    $counter = 0;    $response = array();    while (false !== ($file = readdir($handle))) {        if ($file != "." && $file != "..") {        	            $response[$counter] = $file;            $counter += 1;        }    }    closedir($handle);	echo json_encode($response);}?>